INSERT INTO `info` VALUES ('26', '关于2018届本科毕业生9月份证书发放时间调整的通知', '2018-09-26', '2018-10-08', '2018-10-09', '各学部、学院：\n由于校学位会召开时间调整为2018年10月8日（暂定），按照相关条例规定，原发放证书时间调整为2018年10月9日（09:00-16:30），请9月份毕业的同学按照调整后的时间来教务处主楼西侧楼208领取毕业证、学位证。7月份毕业生领取学位证。\n特此通知。\n', '<p style=\"text-align: left; line-height: 33px;\"><span style=\"color: black; font-family: &#x5B8B;&#x4F53;; font-size: 16px;\">&#x5404;&#x5B66;&#x90E8;&#x3001;&#x5B66;&#x9662;&#xFF1A;</span></p>\n<p style=\"text-align: left; line-height: 33px; text-indent: 32px;\"><span style=\"color: black; font-family: &#x5B8B;&#x4F53;; font-size: 16px;\">&#x7531;&#x4E8E;&#x6821;&#x5B66;&#x4F4D;&#x4F1A;&#x53EC;&#x5F00;&#x65F6;&#x95F4;&#x8C03;&#x6574;&#x4E3A;2018&#x5E74;10&#x6708;8&#x65E5;&#xFF08;&#x6682;&#x5B9A;&#xFF09;&#xFF0C;&#x6309;&#x7167;</span><span style=\"font-family: &#x5B8B;&#x4F53;; font-size: 16px;\">&#x76F8;&#x5173;&#x6761;&#x4F8B;</span><span style=\"color: black; font-family: &#x5B8B;&#x4F53;; font-size: 16px;\">&#x89C4;&#x5B9A;&#xFF0C;&#x539F;&#x53D1;&#x653E;&#x8BC1;&#x4E66;&#x65F6;&#x95F4;&#x8C03;&#x6574;&#x4E3A;2018&#x5E74;10&#x6708;9&#x65E5;&#xFF08;09:00-16:30&#xFF09;&#xFF0C;&#x8BF7;9&#x6708;&#x4EFD;&#x6BD5;&#x4E1A;&#x7684;&#x540C;&#x5B66;&#x6309;&#x7167;&#x8C03;&#x6574;&#x540E;&#x7684;&#x65F6;&#x95F4;&#x6765;&#x6559;&#x52A1;&#x5904;&#x4E3B;&#x697C;&#x897F;&#x4FA7;&#x697C;208&#x9886;&#x53D6;&#x6BD5;&#x4E1A;&#x8BC1;&#x3001;&#x5B66;&#x4F4D;&#x8BC1;&#x3002;7&#x6708;&#x4EFD;&#x6BD5;&#x4E1A;&#x751F;&#x9886;&#x53D6;&#x5B66;&#x4F4D;&#x8BC1;&#x3002;</span></p>\n<p style=\"text-align: left; line-height: 33px; text-indent: 32px;\"><span style=\"color: black; font-family: &#x5B8B;&#x4F53;; font-size: 16px;\">&#x7279;&#x6B64;&#x901A;&#x77E5;&#x3002;</span></p>\n<p></p>', '[\"29\"]');
